import React, { useEffect, useState } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './App.css'
function ProductComponant() {

    const [products, setproducts] = useState([])

    useEffect(() => {
        fetch("https://fakestoreapi.com/products").then(data => data.json()).then(data => setproducts(data))
    }, [])

    console.log('XKXAS', products)

    return (
        <div>
            <h2>Productss</h2>
            <div className="product-list">
            {products.map((data, index) => (
                    <div key={index} className="product-item">
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" style={{ width: '100%', height: '130px' }} src={data?.image} />
                            <Card.Body>
                                <Card.Title>{data.title}</Card.Title>
                                <Card.Text>
                                    {/* {data.description} */}
                                </Card.Text>
                                <Button className='button' style={{ width: '50%', height: '40px' }} variant="primary">Add to Cart</Button>
                            </Card.Body>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    )
}



export default ProductComponant
