import { createSlice } from "@reduxjs/toolkit";


let initialState = []


const createSlices = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        add(state, action) {
            state.push(action.payload)

        }
    }

})


export default createSlices.reducer;
export const { add } = createSlices.actions
