import React, { useEffect, useState } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './App.css'

import { useDispatch } from 'react-redux';
import { add } from './cartSlice';
function ProductComponant() {
    let dispatch = useDispatch()

    const [products, setproducts] = useState([])

    useEffect(() => {
        fetch("https://fakestoreapi.com/products").then(data => data.json()).then(data => setproducts(data))
    }, [])

    console.log('XKXAS', products)


    const HandleFunction = () => {

        dispatch(add())

    }
    return (
        <div>
            <h2>Productss</h2>
            <div className="product-list">
                {products.map((data, index) => (
                    <div key={index} className="product-item">
                        <Card style={{ width: '18rem' }}>
                            <div className='text-center'>


                                <Card.Img variant="top" style={{ width: '100%', height: '130px' }} src={data?.image} />

                            </div>
                            <Card.Body>
                                <Card.Title>{data.title}</Card.Title>
                                <Card.Text>
                                    {/* {data.description} */}
                                    INR: {data.price}
                                </Card.Text>
                                <Button onClick={HandleFunction} className='button' style={{ cursor: 'pointer', width: '50%', height: '40px' }} variant="primary">Add to Cart</Button>
                            </Card.Body>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    )
}



export default ProductComponant
